package ru.relex.education.addressbook.tests;
// Generated by Selenium IDE
//import org.junit.Test;
import com.thoughtworks.xstream.XStream;
import org.testng.annotations.DataProvider;
import org.testng.annotations.Test;
import ru.relex.education.addressbook.model.GroupData;
import ru.relex.education.addressbook.model.Groups;

import java.io.*;
import java.util.ArrayList;
import java.util.Iterator;
import java.util.List;
import java.util.stream.Collectors;

import static org.hamcrest.CoreMatchers.equalTo;
import static org.hamcrest.MatcherAssert.assertThat;

public class GroupCreateTest extends TestBase{

  @DataProvider
  public Iterator<Object[]> validGroups() throws IOException {
    BufferedReader reader = new BufferedReader(new FileReader(new File("src/test/resources/groups.xml")));
    String xml = "";
    String line = reader.readLine();
    while (line != null) {
      //String[] split = line.split(";");
      //list.add(new Object[] {new GroupData().withName(split[0]).withHeader(split[1]).withHeader(split[2])});
      xml += line;
      line = reader.readLine();
    }
    XStream xstream = new XStream();
    xstream.processAnnotations(GroupData.class);
    List<GroupData> groups = (List<GroupData>) xstream.fromXML(xml);
    return groups.stream().map((g) -> new Object[] {g}).collect(Collectors.toList()).iterator();
  }

  //@Test(enabled=false)
  @Test(dataProvider = "validGroups")
  public void testGropCreation(GroupData group) {
    app.goTo().groupPage();
    Groups before = app.group().all();
    app.group().create(group);
    //compare sets count
    assertThat(app.group().count(), equalTo(before.size() + 1));
    Groups after = app.group().all();
    //set in new group ID for before set and compare sets
    assertThat(after, equalTo(before.withAdded(group.withId(after.stream().mapToInt((g) -> g.getId()).max().getAsInt()))));
  }

  @Test
  public void testBadGropCreation() {
    app.goTo().groupPage();
    Groups before = app.group().all();
    GroupData group = new GroupData().withName("test1'");
    app.group().create(group);
    //compare sets count
    assertThat(app.group().count(), equalTo(before.size()));
    Groups after = app.group().all();
    //set in new group ID for before set and compare sets
    assertThat(after, equalTo(before));

  }
}
