package ru.relex.education.addressbook.tests;
// Generated by Selenium IDE
//import org.junit.Test;
import org.testng.annotations.BeforeMethod;
import org.testng.annotations.Test;
import ru.relex.education.addressbook.model.GroupData;
import ru.relex.education.addressbook.model.Groups;

import static org.hamcrest.CoreMatchers.*;
import static org.hamcrest.MatcherAssert.assertThat;
import static org.testng.Assert.assertEquals;

public class GroupDeleteTest extends TestBase{

  @BeforeMethod
  public void ensurePreconditions() {
    app.goTo().groupPage();
    if (app.group().count() == 0) {
      app.group().create(new GroupData().withName("test").withHeader("test").withFooret("test"));
    }
  }

  @Test
  public void testGroupDelete() {
    Groups before = app.group().all();
    GroupData deletedGroup = before.iterator().next();
    app.group().delete(deletedGroup);
    //compare sets count
    assertEquals(app.group().count(), before.size() - 1);
    Groups after = app.group().all();
    //delete group in before set and compare sets
    assertThat(after, equalTo(before.without(deletedGroup)));
  }
}
